  state: { 
    position: (0, 0) 
  }
  behavior: {
    move(): {
      if (near(target)) {
        send_message("reached_target", "target_reached");
      } else {
        direction = get_direction_to(target);
        position = move_in_direction(direction);
        send_message(position, "location_update");
      }
