  on_receive(cluster_members): {
    // Realiza acciones coordinadas con el cluster de robots cercanos
  }
}

channel target_reached_group: {
  on_receive(): {
    // Realiza acciones coordinadas con el grupo de robots que alcanzaron el objetivo
  }
}
